<?php/** * The Category template for Promotions * * @package ProperWeb * @subpackage RoyalFit * @since RoyalFit 1.0 */?><?php get_header(); ?><div id="aside" class="box">	<ul id="aside-sidebar" class="sidebar">		<?php dynamic_sidebar( 'Page Aside' ); ?>	</ul></div><?php if(have_posts()): while(have_posts()): the_post(); ?>	<?php if ( has_post_thumbnail() ) { $featured_image_url = wp_get_attachment_url( get_post_thumbnail_id() ); } ?>	<div class="box mid-width article">		<h2 class="title"><?php the_title(); ?></h2>		<?php the_content(); ?>	</div><?php endwhile; else : ?>	<div class="box mid-width er404">		<h2 class="title">Нет записей</h2>		<h3>Извините, эта рубрика пока еще не содержит ни одной записи</h3>		<p>Соответсвующий контент будет добавлен по мере возможностей.</p>	</div><?php endif; ?><?php get_footer(); ?>