<?php/** * The Category template * * @package ProperWeb * @subpackage ProperFramework * @since ProperFramework 1.0 */?><?php get_header(); ?><div id="aside" class="box">	<ul id="aside-sidebar" class="sidebar">		<?php dynamic_sidebar( 'Page Aside' ); ?>	</ul></div><?php if(have_posts()): while(have_posts()): the_post(); ?>	<div class="box mid-width article">		<h2 class="title"><a class="to-article" href="<?php the_permalink(); ?>"><?php the_title(); ?></a></h2>		<?php if ( has_post_thumbnail() ) : ?>			<?php	the_post_thumbnail( 'thumbnail', array( 'class' => 'thumbnail alignleft') ); ?>		<?php endif; ?>		<?php 			the_excerpt(); 		?>	</div><?php endwhile; endif; ?><?php get_footer(); ?>