<?php/** * The Post template * * @package ProperWeb * @subpackage ProperFramework * @since ProperFramework 1.0 */?><?php get_header(); ?><?php if(have_posts()): while(have_posts()): the_post(); ?>        <?php if ( has_post_thumbnail() ) : ?>        <div class="row">            <div class="large-12 columns">                <div class="featured-image">                <?php the_post_thumbnail( 'post-featured-image' ); ?>                </div>            </div>        </div>    <?php endif; ?>            <div class="row row-with-sidebar">        <div class="cell">            <div class="article article-with-sidebar box full-width">                <h2 class="title"><?php the_title(); ?></h2>                <div class="row meta-data">                    <p class="small-6 columns secondary"><?php _e('Posted by','properweb'); ?> <b><?php the_author_posts_link(); ?></b></p>                    <p class="small-6 columns secondary text-right">                        <time datetime="<?php the_date(); ?>" class="post-date">                            <?php the_time( get_option( 'date_format' ) ); ?>                        </time>                    </p>                    <p class="line small-12 columns"></p>                    </div>                <?php the_content(); ?>            </div>                        <?php if (comments_open()) : ?>            <br>                <div id="comments_wrap" class="article article-with-sidebar box full-width">                    <h2 class="title"><?php _e('Comments','properweb'); ?></h2>                    <?php comments_template(); ?>                </div>            <?php endif; ?>                    </div>            <?php get_sidebar(); ?>        </div>    <?php endwhile; endif; ?><?php get_footer(); ?>